import 'package:flutter/material.dart';
import 'package:provider/provider.dart';
import 'package:ktebxana/theme/theme_provider.dart';
import 'package:firebase_auth/firebase_auth.dart';
import 'package:cloud_firestore/cloud_firestore.dart';

class ReadBookScreen extends StatefulWidget {
  final String bookTitle;
  final String bookCategory;

  const ReadBookScreen({
    super.key,
    required this.bookTitle,
    required this.bookCategory,
  });

  @override
  _ReadBookScreenState createState() => _ReadBookScreenState();
}

class _ReadBookScreenState extends State<ReadBookScreen> {
  int currentPage = 0;
  late List<String> bookPages;
  final TextEditingController _commentController = TextEditingController();
  double _rating = 0;
  bool _isSubmitting = false;

  @override
  void initState() {
    super.initState();
    bookPages = _generateBookContent();
  }

  @override
  void dispose() {
    _commentController.dispose();
    super.dispose();
  }

  Future<void> _submitReview() async {
    if (_rating == 0) {
      ScaffoldMessenger.of(context).showSnackBar(
        const SnackBar(
          content: Text('تکایە نمرە بدە'),
          backgroundColor: Colors.red,
        ),
      );
      return;
    }

    setState(() {
      _isSubmitting = true;
    });

    try {
      final user = FirebaseAuth.instance.currentUser;
      if (user != null) {
        await FirebaseFirestore.instance.collection('book_reviews').add({
          'userId': user.uid,
          'userEmail': user.email,
          'bookTitle': widget.bookTitle,
          'bookCategory': widget.bookCategory,
          'rating': _rating,
          'comment': _commentController.text,
          'timestamp': FieldValue.serverTimestamp(),
        });

        if (mounted) {
          ScaffoldMessenger.of(context).showSnackBar(
            const SnackBar(
              content: Text('ڕەخنەت بە سەرکەوتوویی تۆمارکرا'),
              backgroundColor: Colors.green,
            ),
          );
          _commentController.clear();
          setState(() {
            _rating = 0;
          });
        }
      }
    } catch (e) {
      if (mounted) {
        ScaffoldMessenger.of(context).showSnackBar(
          const SnackBar(
            content: Text('هەڵەیەک ڕوویدا، تکایە دووبارە هەوڵ بدەوە'),
            backgroundColor: Colors.red,
          ),
        );
      }
    } finally {
      if (mounted) {
        setState(() {
          _isSubmitting = false;
        });
      }
    }
  }

